  // Handle edge case of empty array
  if (nums.length === 0) return 0;

  // Initialize our variables with the first element
  let currentSum = nums[0];
  let maxSum = nums[0];

  // Start from the second element (if it exists)
  for (let i = 1; i < nums.length; i++) {
    // For each element, we have two choices:
    // 1. Add it to our current subarray
    // 2. Start a new subarray beginning with this element
    // We choose whichever gives a larger sum
    currentSum = Math.max(nums[i], currentSum + nums[i]);

    // Update the maximum sum we've seen so far
    maxSum = Math.max(maxSum, currentSum);
  }

  return maxSum;
}

