function mergeTwoLists(list1, list2) {
  // Create a dummy head to simplify edge cases
  const dummy = new ListNode(-1);
  let current = dummy;

  // Traverse both lists and compare nodes
  while (list1 !== null && list2 !== null) {
    // Choose the node with the smaller value
    if (list1.val <= list2.val) {
      current.next = list1;
      list1 = list1.next;
    } else {
      current.next = list2;
      list2 = list2.next;
    }

    // Move the pointer forward
    current = current.next;
  }

  // If one list is longer than the other, append the remaining nodes
  current.next = list1 === null ? list2 : list1;

  // Return the head of the merged list (skip dummy node)
  return dummy.next;
}

