  // Edge case: empty array or single price
  if (prices.length <= 1) return 0;

  let minPrice = prices[0];
  let maxProfit = 0;

  for (let i = 1; i < prices.length; i++) {
    // If current price is lower than min price seen so far, update min price
    // This represents a better day to buy
    if (prices[i] < minPrice) {
      minPrice = prices[i];
    } 
    // If selling at today's price would give better profit, update max profit
    else if (prices[i] - minPrice > maxProfit) {
      maxProfit = prices[i] - minPrice;
    }
  }

  return maxProfit;
}

