  if (n <= 2) return n;

  let prev1 = 1;  // Ways to climb 1 step
  let prev2 = 2;  // Ways to climb 2 steps

  for (let i = 3; i <= n; i++) {
    const current = prev1 + prev2;
    prev1 = prev2;
    prev2 = current;
  }

  return prev2;
}

