 */
function climbStairs(n) {
  // Base cases
  if (n <= 0) return 0;
  if (n === 1) return 1;
  if (n === 2) return 2;

  // Use optimized space complexity approach
  let oneStepBefore = 2;  // Ways to climb n-1 stairs
  let twoStepsBefore = 1; // Ways to climb n-2 stairs
  let allWays = 0;

  // Starting from step 3, build up the solution
  for (let i = 3; i <= n; i++) {
    // Ways to reach current step = ways to reach from one step before + ways to reach from two steps before
    allWays = oneStepBefore + twoStepsBefore;

    // Update for the next iteration
    twoStepsBefore = oneStepBefore;
    oneStepBefore = allWays;
  }

  return allWays;
}

