 * Definition for a binary tree node.
 * function TreeNode(val, left, right) {
 *     this.val = (val===undefined ? 0 : val)
 *     this.left = (left===undefined ? null : left)
 *     this.right = (right===undefined ? null : right)
 * }
 */
/**
 * @param {TreeNode} root
 * @return {boolean}
 */
function isSymmetric(root) {
  // Empty tree is symmetric
  if (!root) return true;

  // Helper function to check if two subtrees are mirrors of each other
  function isMirror(leftSubtree, rightSubtree) {
    // If both subtrees are empty, they're symmetric
    if (!leftSubtree && !rightSubtree) return true;

    // If only one subtree is empty, they're not symmetric
    if (!leftSubtree || !rightSubtree) return false;

    // Check if current nodes have same value and their subtrees are mirrors
    return (leftSubtree.val === rightSubtree.val) && 
           isMirror(leftSubtree.left, rightSubtree.right) && 
           isMirror(leftSubtree.right, rightSubtree.left);
  }

  // Check if the left and right subtrees of the root are mirrors
  return isMirror(root.left, root.right);
}

