 * function TreeNode(val, left, right) {
 *     this.val = (val===undefined ? 0 : val)
 *     this.left = (left===undefined ? null : left)
 *     this.right = (right===undefined ? null : right)
 * }
 */
/**
 * @param {TreeNode} root
 * @return {boolean}
 */
function isBalanced(root) {
  // Helper function that returns the height of a subtree if it's balanced,
  // or -1 if it's not balanced
  function getHeight(node) {
    // Base case: null node has height 0
    if (node === null) return 0;

    // Check the height of left subtree
    const leftHeight = getHeight(node.left);
    if (leftHeight === -1) return -1; // Left subtree is not balanced

    // Check the height of right subtree
    const rightHeight = getHeight(node.right);
    if (rightHeight === -1) return -1; // Right subtree is not balanced

    // Check if current node is balanced
    if (Math.abs(leftHeight - rightHeight) > 1) return -1;

    // Return the height of current subtree
    return Math.max(leftHeight, rightHeight) + 1;
  }

  // A tree is balanced if getHeight doesn't return -1
  return getHeight(root) !== -1;
}
