 * @return bool
 */
bool isValid(string s) {
    // Edge case: empty string is considered valid
    if (s.empty()) return true;

    // Edge case: odd length strings can't be valid as brackets come in pairs
    if (s.length() % 2 != 0) return false;

    // Map opening brackets to their corresponding closing brackets
    unordered_map<char, char> bracketPairs = {
        {'(', ')'},
        {'[', ']'},
        {'{', '}'}
    };

    stack<char> st;

    for (char c : s) {
        // If it's an opening bracket, push to stack
        if (bracketPairs.find(c) != bracketPairs.end()) {
            st.push(c);
        } else {
            // If it's a closing bracket
            // First check if stack is empty (no matching opening bracket)
            if (st.empty()) return false;

            // Get the last opening bracket
            char lastBracket = st.top();
            st.pop();

            // Check if the closing bracket matches the last opening bracket
            if (bracketPairs[lastBracket] != c) return false;
        }
    }

    // Valid only if all brackets were matched (stack is empty)
    return st.empty();
}

