    if (!head || !head->next) return false;

    // Initialize two pointers - slow moves by 1, fast moves by 2
    ListNode *slow = head;
    ListNode *fast = head;

    // Keep moving until fast reaches the end
    while (fast && fast->next) {
        // Move slow one step
        slow = slow->next;
        // Move fast two steps
        fast = fast->next->next;

        // If they meet, there's a cycle
        if (slow == fast) {
            return true;
        }
    }

    // If fast pointer reached the end, there's no cycle
    return false;
}

