TreeNode* sortedArrayToBST(vector<int>& nums) {
    return buildBST(nums, 0, nums.size() - 1);
}

TreeNode* buildBST(vector<int>& nums, int left, int right) {
    if (left > right) return nullptr;

    // Find the middle element
    int mid = left + (right - left) / 2;

    // Create the root node with the middle element
    TreeNode* root = new TreeNode(nums[mid]);

    // Recursively build left and right subtrees
    root->left = buildBST(nums, left, mid - 1);
    root->right = buildBST(nums, mid + 1, right);

    return root;
}

TreeNode* sortedArrayToBST(vector<int>& nums) {
    if (nums.empty()) return nullptr;

    // Use a custom struct to track the state
    struct Element {
        int start, end;
        TreeNode* node;
        TreeNode** insert_position;
    };

    stack<Element> st;
    TreeNode* root = nullptr;

    // Initialize with the entire array range
    st.push({0, (int)nums.size() - 1, nullptr, &root});

    while (!st.empty()) {
        Element cur = st.top();
        st.pop();

        int mid = cur.start + (cur.end - cur.start) / 2;
        TreeNode* node = new TreeNode(nums[mid]);

        // Link this node to its parent
        *cur.insert_position = node;

        // Process right subtree (will be processed after left)
        if (mid + 1 <= cur.end) {
            st.push({mid + 1, cur.end, nullptr, &(node->right)});
        }

        // Process left subtree
        if (cur.start <= mid - 1) {
            st.push({cur.start, mid - 1, nullptr, &(node->left)});
        }
    }

    return root;
}

