    function<int(TreeNode*)> height = [&](TreeNode* node) -> int {
        if (!node) return 0;
        return max(height(node->left), height(node->right)) + 1;
    };

    // Check if tree is balanced
    function<bool(TreeNode*)> checkBalance = [&](TreeNode* node) -> bool {
        if (!node) return true;

        int leftHeight = height(node->left);
        int rightHeight = height(node->right);

        return abs(leftHeight - rightHeight) <= 1 && 
               checkBalance(node->left) && 
               checkBalance(node->right);
    };

    return checkBalance(root);
}

bool isBalanced(TreeNode* root) {
    // Helper function that returns height if balanced, -1 if not balanced
    function<int(TreeNode*)> checkHeight = [&](TreeNode* node) -> int {
        if (!node) return 0;

        int leftHeight = checkHeight(node->left);
        // If left subtree is unbalanced, propagate the result
        if (leftHeight == -1) return -1;

        int rightHeight = checkHeight(node->right);
        // If right subtree is unbalanced, propagate the result
        if (rightHeight == -1) return -1;

        // Check if current node is balanced
        if (abs(leftHeight - rightHeight) > 1) {
            return -1;  // Current node is unbalanced
        }

        // Return height of current node
        return max(leftHeight, rightHeight) + 1;
    };

    return checkHeight(root) != -1;
}

